import React, { useState } from 'react';
import pdfMake from 'pdfmake/build/pdfmake';
import vfsFonts from 'pdfmake/build/vfs_fonts';
import Button from '@material-ui/core/Button';
// modal imports
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import CircularProgress from '@material-ui/core/CircularProgress';

const useStyles = makeStyles(theme => ({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
  offset: theme.mixins.toolbar,
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    borderRadius: 5,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function PdfMakeKit({ cliente, values, produtos, parcelas }) {
  function dataAtualFormatada(input) {
    const data = new Date(input);
    data.setDate(data.getDate() + 1);
    const options = { year: 'numeric', month: 'numeric', day: 'numeric' };
    return data.toLocaleDateString('pt-BR', options);
  }

  const productsFormat = produtoParams =>
    produtoParams.map(produto => [
      { text: produto.product.codigo, style: 'centerLine' },
      { text: produto.product.descricao, style: 'centerLine' },
      { text: produto.product.desc_grupo, style: 'centerLine' },
      { text: produto.qtd, style: 'centerLine' },
      {
        text: produto.value.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL',
        }),
        style: 'centerLine',
      },
      {
        text: (produto.value * produto.qtd).toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL',
        }),
        style: 'centerLine',
      },
    ]);

  let paymentsFormat = parcelasParams => {
    const arr1 = [
      [
        {
          text: 'FINANCEIRO',
          alignment: 'center',
          bold: true,
          colSpan: 4,
          fontSize: 9,
          fillColor: '#dddddd',
        },
        {},
        {},
        {},
      ],
      [
        { text: 'PARCELA', style: 'centerHeader' },
        { text: 'DATA', style: 'centerHeader' },
        { text: 'FORMA DE PAGTO', style: 'centerHeader' },
        { text: 'VALOR', style: 'centerHeader' },
      ],
    ];
    const arr2 = parcelasParams.map(parcela => [
      { text: parcelasParams.indexOf(parcela) + 1, style: 'centerLine' },
      { text: dataAtualFormatada(parcela.date), style: 'centerLine' },
      { text: parcela.condition, style: 'centerLine' },
      {
        text: parcela.value.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL',
        }),
        style: 'centerLine',
      },
    ]);
    return [...arr1, ...arr2];
  };

  const pontExtraFormat = () => {
    if (values.pont_extra) {
      return [
        { text: 'PONTEIRA EXTRA: ', bold: true },
        'SIM',
        '\n',
        { text: 'QUANTIDADE: ', bold: true },
        values.qtd_extra,
        '\n',
        { text: 'TIPO: ', bold: true },
        values.tipo_extra,
        '\n',
      ];
    }
    return [];
  };

  const infoAdd = input => {
    if (input) {
      return [
        {
          text: 'Informações adicionais:',
          marginTop: 2,
          style: 'header',
          fontSize: 9,
        },
        {
          table: {
            widths: '*',
            body: [
              [
                {
                  text: input,
                },
              ],
            ],
          },
        },
      ];
    }

    return [];
  };

  const mapProducts = produtos.map(produto => produto.value * produto.qtd);
  const sumProducts =
    mapProducts.length > 0 ? mapProducts.reduce((a, b) => a + b) : 0;
  const mapPayments = parcelas.map(parcela => parcela.value);
  let sumPayments =
    mapPayments.length > 0
      ? Number(parseFloat(mapPayments.reduce((a, b) => a + b)).toFixed(2))
      : 0;

  const infoAdd01 = infoAdd(values.info_ad_produtos);
  const infoAdd02 = infoAdd(values.info_ad_hidraulico);
  const infoAdd03 = values.payment_type
    ? infoAdd(values.info_ad_pagamento)
    : '';

  const ponteira = pont => {
    if (pont) {
      return [
        { text: 'TIPO DE PONTEIRA: ', bold: true },
        values.tipo_ponteira,
        '\n\n',
      ];
    }
    return [];
  };

  const ponteiraValue = ponteira(values.tipo_ponteira);

  const paymentType = input => {
    if (input) {
      return;
    }
    paymentsFormat = () => [
      [
        {
          text: 'FINANCEIRO',
          alignment: 'center',
          bold: true,
          colSpan: 4,
          fontSize: 9,
          fillColor: '#dddddd',
        },
        {},
        {},
        {},
      ],
      [
        {
          text: [
            values.entrada
              ? `Entrada de ${values.entrada.toLocaleString('pt-br', {
                  style: 'currency',
                  currency: 'BRL',
                })}`
              : 'Sem entrada',
            values.num_parcelas &&
              ` / ${values.num_parcelas}x parcelas de ${(
                (sumProducts - values.entrada) /
                values.num_parcelas
              ).toLocaleString('pt-br', {
                style: 'currency',
                currency: 'BRL',
              })} `,
            values.num_parcelas &&
              (values.parcelas_type === 'ddl' ? ' (DDL) ' : ''),
            values.int_parcelas && `a cada ${values.int_parcelas} dias.`,
            values.info_ad_pagamentoAuto && `\n${values.info_ad_pagamentoAuto}`,
          ],
          colSpan: 4,
        },
        {},
        {},
        {},
      ],
    ];
    if (values.num_parcelas && values.valor_parcelas) {
      sumPayments =
        values.entrada + values.num_parcelas * values.valor_parcelas;
    } else {
      sumPayments = sumProducts;
    }
  };

  paymentType(values.payment_type);

  const sumProductsFormated = sumProducts.toLocaleString('pt-br', {
    style: 'currency',
    currency: 'BRL',
  });

  const sumPaymentsFormated = sumPayments.toLocaleString('pt-br', {
    style: 'currency',
    currency: 'BRL',
  });

  const hoje = new Date();
  hoje.setDate(hoje.getDate() - 1);

  const data_pc =
    values.data_pc !== undefined
      ? values.data_pc
          .slice(-2)
          .concat('/')
          .concat(values.data_pc.slice(5, 7))
          .concat('/')
          .concat(values.data_pc.slice(0, 4))
      : null;

  const engate = values.engate === true ? 'SIM' : 'NÃO';
  const contrato = values.contrato === 'sim' ? 'SIM' : 'NÃO';
  const pontExtraFormated = pontExtraFormat();
  const formattedProducts = productsFormat(produtos);
  const formattedPayments = paymentsFormat(parcelas);

  const { vfs } = vfsFonts.pdfMake;
  pdfMake.vfs = vfs;

  const documentDefinition = {
    pageSize: 'A4',
    pageOrientation: 'portrait',
    content: [
      {
        columns: [
          {
            image: 'logo',
            width: 100,
          },
          {
            width: 'auto',
            text: [
              {
                text:
                  '\nAGF IMPORTAÇÃO, EXPORTAÇÃO E COMERCIALIZAÇÃO DE MÁQUINAS E ACESSÓRIOS LTDA\n',
                bold: true,
              },
              'CNPJ: 11.681.470/0001-84 | INSCRIÇÃO ESTADUAL: 530.051.442.114\n',
              'MATRIZ: Rod. SP 346, km 202,5 - Distrito Industrial, Espirito Santo do Pinhal - SP\n',
              'FILIAL: Rod. Engenheiro Fabiano Vivacqua, 441, Marbrasa, Cachoeiro Itapemirim - ES',
            ],
            fontSize: 6,
          },
          {
            table: {
              widths: [100],
              body: [
                [
                  {
                    text: 'Nº DO PEDIDO',
                    alignment: 'center',
                    bold: true,
                    fontSize: 5,
                    fillColor: '#dddddd',
                  },
                ],
                [
                  {
                    text: values.num_pedido || ' ',
                    alignment: 'center',
                    fontSize: 5,
                  },
                ],
                [
                  {
                    text: 'DATA DO PEDIDO',
                    alignment: 'center',
                    bold: true,
                    fontSize: 5,
                    fillColor: '#dddddd',
                  },
                ],
                [{ text: data_pc || ' ', alignment: 'center', fontSize: 5 }],
              ],
              align: 'center',
            },
          },
        ],
      },
      {
        text: 'PEDIDO DE COMPRA\n',
        fontSize: 14,
        alignment: 'center',
      },
      {
        text: [
          { text: 'VENDEDOR: ', bold: true },
          values.vendedor,
          { text: '\nNATUREZA DE OPERAÇÃO: ', bold: true },
          values.natureza_operacao,
          '\n\n',
        ],
      },
      {
        table: {
          widths: '*',
          body: [
            [
              {
                text: 'DADOS DO CLIENTE',
                alignment: 'center',
                bold: true,
                colSpan: 2,
                fontSize: 9,
                fillColor: '#dddddd',
              },
              {},
            ],
            [
              {
                text: [
                  { text: 'Razão Social: ', bold: true },
                  cliente.razao_social,
                  '\n',
                  { text: 'CNPJ: ', bold: true },
                  cliente.cnpj,
                  '\n',
                  { text: 'Inscrição Estadual: ', bold: true },
                  cliente.inscricao_estadual,
                  '\n',
                  { text: 'Telefone: ', bold: true },
                  cliente.telefone,
                  '\n',
                  { text: 'Nome do contato: ', bold: true },
                  values.nome_contato,
                  '\n',
                  { text: 'Cargo do contato: ', bold: true },
                  values.cargo_contato,
                  '\n',
                  { text: 'Email: ', bold: true },
                  values.email_contato,
                ],
              },
              {
                text: [
                  { text: 'CÓDIGO: ', bold: true },
                  cliente.codigo_cliente,
                  '\n',
                  { text: 'Endereço: ', bold: true },
                  cliente.endereco,
                  '\n',
                  { text: 'Bairro: ', bold: true },
                  cliente.bairro,
                  '\n',
                  { text: 'Município: ', bold: true },
                  cliente.municipio,
                  '\n',
                  { text: 'UF: ', bold: true },
                  cliente.uf,
                  '\n',
                  { text: 'CEP: ', bold: true },
                  cliente.cep,
                  '\n',
                  { text: 'Telefone: ', bold: true },
                  cliente.telefone,
                ],
              },
            ],
          ],
          align: 'center',
        },
      },
      '\n',
      {
        table: {
          widths: [60, '*', 60, 30, 80, 80],
          body: [
            [
              {
                text: 'PRODUTOS',
                alignment: 'center',
                bold: true,
                colSpan: 6,
                fontSize: 9,
                fillColor: '#dddddd',
              },
              {},
              {},
              {},
              {},
              {},
            ],
            [
              { text: 'CÓDIGO', style: 'centerHeader' },
              { text: 'PRODUTO', style: 'centerHeader' },
              { text: 'GRUPO', style: 'centerHeader' },
              { text: 'QTD', style: 'centerHeader' },
              { text: 'PREÇO UNIT', style: 'centerHeader' },
              { text: 'TOTAL', style: 'centerHeader' },
            ],
            ...formattedProducts,
          ],
        },
      },
      {
        text: ['TOTAL: ', sumProductsFormated, '\n'],
        marginTop: 2,
        style: 'header',
        fontSize: 9,
        alignment: 'right',
      },
      ...infoAdd01,
      '\n',
      {
        table: {
          widths: '*',
          body: [
            [
              {
                text: 'KIT HIDRÁULICO',
                alignment: 'center',
                colSpan: 3,
                bold: true,
                fontSize: 9,
                fillColor: '#dddddd',
              },
              {},
              {},
            ],
            [
              {
                text: [
                  values.kit,
                  '\n\n',
                  { text: 'SOBRE A MÁQUINA: ', bold: true },
                  values.maquina,
                  '\n',
                  { text: 'MODELO: ', bold: true },
                  values.modelo,
                  '\n',
                  { text: 'ANO: ', bold: true },
                  values.ano,
                  '\n',
                ],
              },
              {
                text: [
                  { text: 'MÁQUINA POSSUI ENGATE RÁPIDO: ', bold: true },
                  engate,
                  '\n',
                  { text: 'INFORMAÇÕES RELEVANTES: ', bold: true },
                  values.informacoes_relevantes,
                  '\n',
                  ...ponteiraValue,
                  ...pontExtraFormated,
                ],
              },
              {
                text: [
                  {
                    text: 'USO EXCLUSIVO PARA ROMPEDORES HIDRÁULICOS: ',
                    bold: true,
                  },
                  values.condicao,
                  '\n',
                ],
              },
            ],
          ],
          align: 'center',
        },
      },
      ...infoAdd02,
      '\n',
      {
        table: {
          widths: '*',
          body: [...formattedPayments],
        },
      },
      {
        text: ['TOTAL: ', sumPaymentsFormated, '\n'],
        marginTop: 2,
        style: 'header',
        fontSize: 9,
        alignment: 'right',
      },
      ...infoAdd03,
      '\n',
      {
        table: {
          widths: '*',
          body: [
            [
              { text: 'DADOS BANCÁRIOS PARA TED:', style: 'centerHeader' },
              { text: 'FRETE', style: 'centerHeader' },
              { text: 'CONTRATO', style: 'centerHeader' },
            ],
            [
              {
                text: [
                  { text: 'BANCO ITAÚ: 341' },
                  '\n',
                  { text: 'AG: 1619' },
                  '\n',
                  { text: 'C/C: 34963-0' },
                  '\n',
                ],
              },
              {
                text: [
                  { text: 'FRETE: ', bold: true },
                  values.frete,
                  '\n',
                  { text: 'PREVISÃO DE ENTREGA: ', bold: true },
                  values.delivery_date
                    ? dataAtualFormatada(values.delivery_date)
                    : 'A COMBINAR',
                  '\n',
                  { text: 'DATA PREVISTA DE INSTALAÇÃO: ', bold: true },
                  '3 a 10 dias após a chegada do equipamento',
                  '\n',
                ],
              },
              {
                text: [
                  { text: 'CONTRATO: ', bold: true },
                  contrato,
                  '\n',
                  { text: 'Nº DO CONTRATO: ', bold: true },
                  values.num_contrato,
                  '\n',
                  { text: 'Nº DO PEDIDO: ', bold: true },
                  values.num_pedido,
                  '\n',
                  { text: 'Nº DE NOTA FISCAL: ', bold: true },
                  values.num_nf,
                  '\n',
                  { text: 'Nº DE SÉRIE: ', bold: true },
                  values.ns,
                  '\n',
                ],
              },
            ],
          ],
        },
      },
      '\n\n\n\n\n\n\n\n',
      {
        columns: [
          {
            width: '*',
            text: [
              {
                text: '_________________________________________________',
                bold: true,
              },
              '\n\n',
              { text: cliente.razao_social, bold: true },
              '\n\n',
              { text: 'Data: ______ / ______ / ________', bold: true },
            ],
            alignment: 'center',
            fontSize: 6,
          },
          {
            width: '*',
            text: [
              {
                text: '_________________________________________________',
                bold: true,
              },
              '\n\n',
              { text: 'AGF Equipamentos', bold: true },
              '\n\n',
              { text: 'Data: ______ / ______ / ________', bold: true },
            ],
            alignment: 'center',
            fontSize: 6,
          },
        ],
      },
    ],
    styles: {
      centerHeader: {
        alignment: 'center',
        bold: true,
        fillColor: '#ECECEC',
      },
      centerLine: {
        alignment: 'center',
      },
    },
    defaultStyle: {
      columnGap: 20,
      fontSize: 6,
    },
    images: {
      logo:
        'data:image/png;base64,',
    },
  };
  const [errors, setErrors] = useState({});
  let errorCount = 0;

  // modal
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const PdfGenerator = () => {
    if (!values.vendedor || !values.natureza_operacao) {
      setErrors({
        vendedor: 'Preencha todos os dados obrigatórios do vendedor!',
      });
      errorCount += 1;
    }
    if (!cliente || !values.nome_contato || !values.email_contato) {
      setErrors(prevState => ({
        ...prevState,
        cliente: 'Preencha todos os dados obrigatórios do cliente!',
      }));
      errorCount += 1;
    }
    if (produtos.length <= 0) {
      setErrors(prevState => ({
        ...prevState,
        produto: 'Preencha os produtos!',
      }));
      errorCount += 1;
    }
    if (!values.tipo_contrato) {
      setErrors(prevState => ({
        ...prevState,
        contrato: 'Selecione o tipo de contrato!',
      }));
      errorCount += 1;
    }
    if (values.payment_type && parcelas.length <= 0) {
      setErrors(prevState => ({
        ...prevState,
        produto: 'Preencha os dados de pagamento!',
      }));
      errorCount += 1;
    }
    if (!values.frete) {
      setErrors(prevState => ({
        ...prevState,
        contrato: 'Selecione o tipo de frete!',
      }));
      errorCount += 1;
    }
    if (sumProducts !== sumPayments) {
      setErrors(prevState => ({
        ...prevState,
        contrato:
          'O valor total dos produtos não coincide com o valor total das parcelas!',
      }));
      errorCount += 1;
    }

    async function MakePdf() {
      handleOpen();
      await new Promise(resolve => setTimeout(resolve, 500));
      pdfMake
        .createPdf(documentDefinition)
        .download(`${cliente.razao_social} - ${dataAtualFormatada(hoje)}`);
      handleClose();
    }

    if (errorCount === 0) {
      setErrors({});
      MakePdf();
    }
    errorCount = 0;
  };
  return (
    <>
      <Button
        variant="contained"
        color="primary"
        style={{ margin: 15 }}
        onClick={PdfGenerator}
      >
        Gerar PDF
      </Button>
      {Object.values(errors).map(erro => (
        <div key={erro}>{erro}</div>
      ))}
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <h2>Gerando PDF...</h2>
            <div>
              <CircularProgress style={{ margin: 15 }} />
            </div>

            <Button
              type="button"
              onClick={handleClose}
              variant="contained"
              color="primary"
            >
              Cancelar
            </Button>
          </div>
        </Fade>
      </Modal>
    </>
  );
}
